body.script:
const FINAL_IMAGE_URL = "https://i.imgur.com/QAO5YTY.jpeg";

let mode = "";
let current = 0;

const indicii = [
    { q: "CƒÉpitanul care vrea sƒÉ devinƒÉ Regele Pira»õilor", a: "luffy" },
    { q: "Spadasinul cu trei sƒÉbii", a: "zoro" },
    { q: "Navigatoarea care iube»ôte banii", a: "nami" },
    { q: "BucƒÉtarul care luptƒÉ doar cu picioarele", a: "sanji" }
];

const hardQuestions = [
    { q: "DescifreazƒÉ (Cezar -3): FRPRDUD HVWH SULHWHQLD DGHYHUDWD", a: "comoara este prietenia adevarata" },
    { q: "Traducere binar: 01000011 01001111 01001101 01001111 01000001 01010010 01000001", a: "comoara" }
];

setTimeout(function () {
    document.getElementById("splash").classList.add("hidden");
    document.getElementById("menu").classList.remove("hidden");
}, 3000);

function startIndicii() {
    mode = "indicii";
    current = 0;
    document.getElementById("menu").classList.add("hidden");
    document.getElementById("game").classList.remove("hidden");
    loadQuestion();
}

function startHard() {
    mode = "hard";
    current = 0;
    document.getElementById("menu").classList.add("hidden");
    document.getElementById("game").classList.remove("hidden");
    loadQuestion();
}

function loadQuestion() {
    let list = mode === "indicii" ? indicii : hardQuestions;

    if (current >= list.length) {
        showFinal();
        return;
    }

    document.getElementById("question").innerText = list[current].q;
    document.getElementById("answer").value = "";
}

function checkAnswer() {
    let list = mode === "indicii" ? indicii : hardQuestions;
    let user = document.getElementById("answer").value.trim().toLowerCase();
    let correct = list[current].a.toLowerCase();

    if (user === correct) {
        current++;
        loadQuestion();
    } else {
        alert("Gre»ôit! √éncearcƒÉ din nou.");
    }
}

function showFinal() {
    document.getElementById("game").classList.add("hidden");
    document.getElementById("finalScreen").classList.remove("hidden");
    document.getElementById("finalImage").src = FINAL_IMAGE_URL;
}
